mapper.select:
select count(*)
from gr_member
where mb_id = #{mb_id}

select count(*)
from gr_member
where mb_name = #{mb_name} and mb_email = #{mb_email}

select count(*)
from gr_member
where mb_id = #{mb_id} and mb_email = #{mb_email}

mapper.update:
update gr_member
set mb_check = 'Y'
where mb_id = #{mb_id}

mapper.select:
select count(*)
from gr_member
where mb_id = #{mb_id} and mb_pw = #{mb_pw}

mapper.update:
update gr_member
set mb_pw = #{mb_pw}
where mb_id = #{mb_id}

mapper.select:
select count(*)
from gr_member
where mb_id = #{mb_id} and mb_email = #{mb_email}

mapper.update:
update gr_member
set mb_email = #{mb_email}
where mb_id = #{mb_id}
