mapper.select:
SELECT NT_NO, NT_TITLE, AD_ID, NT_CON, NT_HITS, NT_REGDATE
FROM GR_NOTICE
WHERE NT_NO = #{nt_no}

mapper.update:
UPDATE GR_NOTICE
SET NT_TITLE = #{nt_title}, NT_CON = #{nt_con}, NT_REGDATE = now()
WHERE NT_NO = #{nt_no}

UPDATE GR_NOTICE
SET NT_HITS = NT_HITS+1
WHERE NT_NO = #{nt_no}

mapper.select:
SELECT * FROM GR_NOTICE
ORDER BY NT_NO DESC
LIMIT #{displayPost}, #{postNum}
