mapper.select:
select * from gr_reservation
where MB_ID = #{mb_id}
order by RE_DATE desc

mapper.update:
update gr_reservation
set RE_APPROVAL = '승인'
where RE_NUM = #{re_num};

update gr_reservation
set RE_APPROVAL = '취소'
where RE_NUM = #{re_num};

mapper.select:
select REG_SHOPADDRESS
from gr_registration
where de_licencenum = #{de_licencenum};
